import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Profile() {
  const [user, setUser] = useState({});
  const [bio, setBio] = useState('');
  const [image, setImage] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/auth/user/', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => {
      setUser(res.data);
      setBio(res.data.bio || '');
    });
  }, [token]);

  const handleSave = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('bio', bio);
    if (image) formData.append('profile_image', image);

    await axios.put('http://127.0.0.1:8000/auth/user/', formData, {
      headers: {
        'Authorization': `Token ${token}`,
        'Content-Type': 'multipart/form-data'
      }
    });

    alert("Profile updated!");
  };

  return (
    <div className="profile-page">
      <h2>Welcome, {user.username}</h2>
      {user.profile_image && (
        <img
          src={`http://127.0.0.1:8000${user.profile_image}`}
          alt="Profile"
          width="120"
          style={{ borderRadius: '50%', marginBottom: '1em' }}
        />
      )}
      <form onSubmit={handleSave}>
        <textarea value={bio} onChange={(e) => setBio(e.target.value)} placeholder="Your bio" />
        <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        <button type="submit">Save Profile</button>
      </form>
    </div>
  );
}

export default Profile;
